local Anniversary19DreamCommonView = require("Game.views.anniversary19.Anniversary19DreamCommonView")
---@class Anniversary19BattleCardView : Anniversary19DreamCommonView
local Anniversary19BattleCardView = class('Anniversary19BattleCardView',Anniversary19DreamCommonView)
local anniversary2019Mgr = app.anniversary2019Mgr
function Anniversary19BattleCardView:AddDiffView(exploreModuleId , exploreId)
	local conf = anniversary2019Mgr:GetDreamQuestTypeConfByDreamQuestType(anniversary2019Mgr.dreamQuestType.CARDS_SHUT)
	local oneConf = conf[tostring(exploreModuleId)][tostring(exploreId)]
	local skinId = TTGameUtils.GetConfAt(TTGAME_DEFINE.CONF_TYPE.NPC_DEFINE, oneConf.questId).draw
	local qAvatar = AssetsUtils.GetCardSpineNode({skinId = skinId , scale = 0.8})
	qAvatar:setAnimation(0, 'idle', true)
	qAvatar:setPosition(cc.p(350, 50))
	self.viewData.centerLayout:addChild(qAvatar)
end
function Anniversary19BattleCardView:UpdateUI(exploreModuleId ,  exploreId )
	local anniversary2019Mgr = app.anniversary2019Mgr
	local viewData = self.viewData
	display.commonLabelParams(viewData.resultLabel , {text = app.anniversary2019Mgr:GetPoText(__('打牌获胜可获得'))})
	display.commonLabelParams(viewData.descrLabel , {text  = app.anniversary2019Mgr:GetPoText(__('在梦境中突然出现的乱入者，挑战他的话可以得到双倍的奖励哦！【注意：挑战的机会只有一次。】')) })
	display.commonLabelParams(viewData.titleBtn , {text = app.anniversary2019Mgr:GetPoText(__('乱入者'))})
	display.commonLabelParams(viewData.rightButton , fontWithColor(14 , {text = app.anniversary2019Mgr:GetPoText(__('挑战'))}))
	display.commonLabelParams(viewData.leftButton , fontWithColor(14 , {text = app.anniversary2019Mgr:GetPoText(__('放弃'))}))
	local parserConf = anniversary2019Mgr:GetConfigParse()
	local parameterConf = anniversary2019Mgr:GetConfigDataByName(parserConf.TYPE.PARAMETER)
	local rewardDoubleId = parameterConf.rewardDoubleId
	viewData.goodNode:RefreshSelf({ goodsId = rewardDoubleId , num = 0 })
	viewData.goodNode.infoLabel:setVisible(false)
	self:SetRightCenterLayoutVisible(true)
	self:SetTwoBtn()
end
return Anniversary19BattleCardView

